import { useEffect, useState } from "react";
import { ScrollView,Button, StyleSheet, Text, View } from "react-native";
import { NavigationContainer } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import axios from "axios";

import EventsDetail from "./EventsDetail";
import {API_BASE_URL} from "../Api/BaseURL"
import useGetData from "../Api/useGetData";

const Stack = createNativeStackNavigator();

function Events() {
  return (
    <Stack.Navigator screenOptions={{ headerShown: false }}>
      <Stack.Screen
        name="EventsScreen"
        component={EventsScreen}
        options={{ title: "Events" }}
      />
      <Stack.Screen
        name="EventsDetail"
        component={EventsDetail}
        options={{ title: "Event Details" }}
      />
    </Stack.Navigator>
  );
}
function EventCard({ event }) {
  return (
    <View style={styles.card}>
      <Text style={styles.title}>{event.name}</Text>
      <Text style={styles.text}>{event.dateStart}</Text>
      <Text style={styles.text}>{event.takePlace}</Text>
      <Text style={styles.text}>{event.eventDescription}</Text>
      {/* Add any additional event fields here */}
    </View>
  );
}
function EventsScreen({ navigation, route }) {
  
  // ? API requests
  const [data, loading, error] = useGetData(
    `${API_BASE_URL}/events?offset=0&limit=3`
  );
  if(!loading){
    console.log('got data',data)
  }
  
  // ? decor
  // useEffect(() => {
  //   if (route.params?.post) {
  //     // Post updated, do something with `route.params.post`
  //     // For example, send the post to the server
  //   }
  // }, [route.params?.post]);

  return (
    <ScrollView contentContainerStyle={styles.container}>
    {data.map((event) => (
        <EventCard key={event.eventId} event={event} />
      ))}
      <Text style={{ margin: 10 }}>Post: {route.params?.post}</Text>
      <Button
        title="Event"
        onPress={() => {
          navigation.navigate("EventsDetail", {
            eventID: 86,
            eventName: "Drone light show",
          });
        }}
      />
    </ScrollView>
  );
}


const styles = StyleSheet.create({
  container: {
    flexGrow: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  card: {
    backgroundColor: "white",
    padding: 16,
    borderRadius: 8,
    marginVertical: 8,
    marginHorizontal: 16,
    elevation: 4,
    shadowColor: "#000",
    shadowOpacity: 0.2,
    shadowRadius: 2,
    shadowOffset: {
      width: 0,
      height: 2,
    },
  },
  title: {
    fontSize: 20,
    fontWeight: "bold",
    marginBottom: 8,
  },
  text: {
    fontSize: 16,
    marginBottom: 4,
  },
});

export default Events;
