import React from "react";
import { Button, StyleSheet, Text, View } from "react-native";
import { NavigationContainer } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";

import EventsDetail from "./EventsDetail";

const Stack = createNativeStackNavigator();

const Page = () => {
  <View>
    <Text>This is Events page</Text>
    <Text style={{ margin: 10 }}>Post: {route.params?.post}</Text>
    <Button
      title="Event"
      onPress={() => {
        navigation.navigate("EventsDetail", {
          eventID: 86,
          eventName: "Drone light show",
        });
      }}
    />
  </View>;
};

function Events({ navigation, route }) {
  // React.useEffect(() => {
  //   if (route.params?.post) {
  //     // Post updated, do something with `route.params.post`
  //     // For example, send the post to the server
  //   }
  // }, [route.params?.post]);
  return (
    <Stack.Navigator>
      <Text style={{ margin: 10 }}>Post: {route.params?.post}</Text>
      <Button
        title="Event"
        onPress={() => {
          navigation.navigate("EventsDetail", {
            eventID: 86,
            eventName: "Drone light show",
          });
        }}
      />
      {/* <Stack.Screen name="EventPage" component={Page} /> */}
      <Stack.Screen name="EventsDetail" component={EventsDetail} />
    </Stack.Navigator>
  );
}

export default Events;

